import React, { useEffect, useRef, useState, useContext } from "react";
import Footer from "../../footer/Footer";
import Header from "../../header/Header";
import SideNav from "../../sidenav/SideNav";
import { CopyToClipboard } from "react-copy-to-clipboard";
import Peer from "simple-peer";
import io from "socket.io-client";
import { AuthContext } from "../../../helpers/AuthContext";
import remoteViewingStyles from "./RemoteViewing.module.css";

const socket = io.connect("http://localhost:5000");

const RemoteViewing = () => {
  const { authState } = useContext(AuthContext);

  const [me, setMe] = useState("");
  const [stream, setStream] = useState();
  const [receivingCall, setReceivingCall] = useState(false);
  const [caller, setCaller] = useState("");
  const [callerSignal, setCallerSignal] = useState();
  const [callAccepted, setCallAccepted] = useState(false);
  const [idToCall, setIdToCall] = useState("");
  const [callEnded, setCallEnded] = useState(false);
  const [name, setName] = useState("");

  const myVideo = useRef();
  const userVideo = useRef();
  const connectionRef = useRef();

  useEffect(() => {
    navigator.mediaDevices
      .getUserMedia({ video: true, audio: true })
      .then((stream) => {
        setStream(stream);
        if (myVideo.current) {
          myVideo.current.srcObject = stream;
        }
      });

    socket.on("me", (id) => {
      setMe(id);
    });

    socket.on("callUser", (data) => {
      setReceivingCall(true);
      setCaller(data.from);
      setName(data.name);
      setCallerSignal(data.signal);
    });
  }, []);

  const callUser = (id) => {
    const peer = new Peer({
      initiator: true,
      trickle: false,
      stream: stream,
    });
    peer.on("signal", (data) => {
      socket.emit("callUser", {
        userToCall: id,
        signalData: data,
        from: me,
        name: authState.name,
      });
    });
    peer.on("stream", (stream) => {
      userVideo.current.srcObject = stream;
    });
    socket.on("callAccepted", (signal) => {
      setCallAccepted(true);
      peer.signal(signal);
    });

    connectionRef.current = peer;
  };

  const answerCall = () => {
    setCallAccepted(true);
    const peer = new Peer({
      initiator: false,
      trickle: false,
      stream: stream,
    });
    peer.on("signal", (data) => {
      socket.emit("answerCall", { signal: data, to: caller });
    });
    peer.on("stream", (stream) => {
      userVideo.current.srcObject = stream;
    });

    peer.signal(callerSignal);
    connectionRef.current = peer;
  };

  const leaveCall = () => {
    setCallEnded(true);
    if (connectionRef.current) {
      connectionRef.current.destroy();
    }
    window.location.reload();
  };

  return (
    <div className={remoteViewingStyles["remote-viewing-page"]}>
      <SideNav remoteViewing={true} />
      <div className={remoteViewingStyles["content-container"]}>
        <Header />
        <div className={remoteViewingStyles["remote-viewing-container"]}>
          <h1 className={remoteViewingStyles["remote-viewing-heading"]}>
            Remote Viewing
          </h1>
          <div className={remoteViewingStyles["video-container"]}>
            <div className={remoteViewingStyles["video"]}>
              {stream && (
                <video
                  playsInline
                  muted
                  ref={myVideo}
                  autoPlay
                  style={{ width: "300px" }}
                />
              )}
            </div>
            <div className="video">
              {callAccepted && !callEnded ? (
                <video
                  playsInline
                  ref={userVideo}
                  autoPlay
                  style={{ width: "300px" }}
                />
              ) : null}
            </div>
          </div>
          <div className={remoteViewingStyles["myId"]}>
            <p>{authState.name}</p>
            <CopyToClipboard text={me} style={{ marginBottom: "2rem" }}>
              <button>Copy ID</button>
            </CopyToClipboard>

            <input
              id="filled-basic"
              label="ID to call"
              type="text"
              value={idToCall}
              onChange={(e) => setIdToCall(e.target.value)}
            />
            <div className={remoteViewingStyles["call-button"]}>
              {callAccepted && !callEnded ? (
                <button onClick={leaveCall}>End Call</button>
              ) : (
                <button onClick={() => callUser(idToCall)}>phone icon</button>
              )}
              {idToCall}
            </div>
          </div>
          <div>
            {receivingCall && !callAccepted ? (
              <div className={remoteViewingStyles["caller"]}>
                <h1>{name} is calling...</h1>
                <button onClick={answerCall}>Answer</button>
              </div>
            ) : null}
          </div>
        </div>
        <Footer />
      </div>
    </div>
  );
};

export default RemoteViewing;
